mapper.update:
UPDATE MP_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

mapper.select:
SELECT STORED_FILE_NAME, ORG_FILE_NAME
FROM MP_FILE
WHERE FILE_NO = #{FILE_NO}

mapper.update:
	UPDATE BBS SET 
    FILEFOUND = 1
    WHERE BBSID = #{BBSID}

mapper.select:
SELECT * FROM (SELECT bbsID, bbsTitle, userID, bbsDate, bbsContent, bbsAvailable, fileFound, ROW_NUMBER() OVER(ORDER BY bbsId DESC) rn FROM BBS
bbsAvailable = 1
AND LOWER(${searchType}) LIKE LOWER('%${word}%')
) WHERE ${startList} <= rn and rn <= ${endList}

mapper.update:
	UPDATE BBS SET 
    BBSTITLE = #{bbsTitle}, BBSCONTENT = #{bbsContent} 
    WHERE BBSID = #{bbsID}

UPDATE BBS SET 
BBSAVAILABLE = 0 
WHERE BBSID = #{bbsID}

mapper.select:
SELECT USERID, USERPASSWORD
FROM USERS
WHERE USERID = #{userID}
AND USERPASSWORD = #{userPassword}
